// Temperature
function cToF(&$temp, $field){
	if(!isset($temp[$field])) return;
	$temp[$field] = cToFDirect($temp[$field]);
}

function fToC(&$temp, $field){
	if(!isset($temp[$field])) return;
	$temp[$field] = fToCDirect($temp[$field]);
}

function cToFrel(&$temp, $field){
	if(!isset($temp[$field])) return;
	$temp[$field] = round((float)((9/5)*($temp[$field])), 1);
}

function fToCrel(&$temp, $field){
	if(!isset($temp[$field])) return;
	$temp[$field] = round((float)((5/9)*($temp[$field])), 1);
}

function fToCDirect($field){
	return round((float)((5/9)*($field-32)), 1);
}

function cToFDirect($field){
	return  number_format((float)$field*(9/5)+32,1);
}

// Pressure
function mbToin(&$barom, $field, $conversion){
	if(!isset($barom[$field])) return;
	$barom[$field] = round((float)(0.02952999*$barom[$field] * $conversion), 2);
}

function inTomb(&$barom, $field, $conversion){
	if(!isset($barom[$field])) return;
	$barom[$field] = round((float)(33.86388158*$barom[$field] * $conversion), 2);
}

// Depth
function mmToin(&$divum, $field){
	if(!isset($divum[$field])) return;
	$divum[$field] = number_format(0.03937008*$divum[$field], 2);
}

function inTomm(&$temp, $field){
